import { useState } from "react";
import useInput from "../hooks/use-Input";

const BasicForm = (props) => {
  const {
    enteredValue: Fname,
    valueChangeHandler: FnameChangeHandler,
    InputIsValid: FnameInputIsValid,
    BlurHandler: FnameBlurHandler,
    reset: FnameReset,
  } = useInput((value) => value.trim() !== "");

  const {
    enteredValue: Lname,
    valueChangeHandler: LnameChangeHandler,
    InputIsValid: LnameInputIsValid,
    BlurHandler: LnameBlurHandler,
    reset: LnameReset,
  } = useInput((value) => value.trim() !== "");

  const {
    enteredValue: Email,
    valueChangeHandler: EmailChangeHandler,
    InputIsValid: EmailInputIsValid,
    BlurHandler: EmailBlurHandler,
    reset: EmailReset,
  } = useInput((value) => value.includes(""));

  const FnameClass = FnameInputIsValid ? "ok" : "no";
  const LnameClass = LnameInputIsValid ? "ok" : "no";
  const EmailClass = EmailInputIsValid ? "ok" : "no";

  const SubmitIsValid =
    FnameInputIsValid && LnameInputIsValid && EmailInputIsValid;

  const submitHandler = (event) => {};

  return (
    <form onSubmit={submitHandler}>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input
            className={FnameClass}
            type="text"
            id="Fname"
            value={Fname}
            onChange={FnameChangeHandler}
            onBlur={FnameBlurHandler}
          />
          {!FnameInputIsValid && <p className="ErrorP">First Name is Error</p>}
        </div>

        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input
            className={LnameClass}
            type="text"
            id="Lname"
            value={Lname}
            onChange={LnameChangeHandler}
            onBlur={LnameBlurHandler}
          />
          {!LnameInputIsValid && <p className="ErrorP">Last Name is Error</p>}
        </div>
      </div>

      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input
          className={EmailClass}
          type="email"
          id="email"
          value={Email}
          onChange={EmailChangeHandler}
          onBlur={EmailBlurHandler}
        />
        {!EmailInputIsValid && <p className="ErrorP">E-mail is must have @</p>}
      </div>

      <div className="form-actions">
        <button disabled={!SubmitIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
