import { useState } from "react";

const BasicForm = (props) => {
  const [FnameIsValid, setFnameIsValid] = useState(true);
  const [LnameIsValid, setLnameIsValid] = useState(true);
  const [EmailIsValid, setEmailIsValid] = useState(true);

  // const [FnameInputTouched, setFnameInputTouched] = useState(false);
  // const [LnameInputTouched, setLnameInputTouched] = useState(false);
  // const [EmailInputTouched, setEmailInputTouched] = useState(false);

  // const FnameInputIsValid = FnameIsValid && !FnameInputTouched;
  // const LnameInputIsValid = LnameIsValid && !LnameInputTouched;
  // const EmailInputIsValid = EmailIsValid && !EmailInputTouched;

  const submitIsValid = FnameIsValid && LnameIsValid && EmailIsValid;

  const FnameClass = FnameIsValid ? "ok" : "no";

  const FnameInputHandler = (event) => {
    if (event.target.value.trim() !== "") {
      setFnameIsValid(true);
    } else {
      setFnameIsValid(false);
    }
  };

  const LnameInputHandler = (event) => {
    if (event.target.value.trim() !== "") {
      setLnameIsValid(true);
    } else {
      setLnameIsValid(false);
    }
  };

  const EmailInputHandler = (event) => {
    if (event.target.value.trim() !== "") {
      setEmailIsValid(true);
    } else {
      setEmailIsValid(false);
    }
  };

  return (
    <form>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input
            className={FnameClass}
            type="text"
            id="Fname"
            onChange={FnameInputHandler}
            onBlur={FnameInputHandler}
          />
          {!FnameIsValid && <p className="ErrorP">First Name is Error</p>}
        </div>

        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input
            type="text"
            id="Lname"
            onChange={LnameInputHandler}
            onBlur={LnameInputHandler}
          />
          {!LnameIsValid && <p className="ErrorP">Last Name is Error</p>}
        </div>
      </div>

      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input
          type="email"
          id="email"
          onChange={EmailInputHandler}
          onBlur={EmailInputHandler}
        />
        {!EmailIsValid && <p className="ErrorP">E-mail is must have @</p>}
      </div>

      <div className="form-actions">
        <button disabled={FnameIsValid == false}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
