import { useState } from "react";

const BasicForm = (props) => {
  const [FnameIsValid, setFnameIsValid] = useState(false);
  const [LnameIsValid, setLnameIsValid] = useState(false);
  const [EmailIsValid, setEmailIsValid] = useState(false);

  const submitIsValid = FnameIsValid && LnameIsValid && EmailIsValid;

  console.log("submitIsValid : ", submitIsValid);

  const FnameInputHandler = (event) => {
    if (event.target.value.trim() !== "") {
      setFnameIsValid(true);
    }
  };

  const LnameInputHandler = (event) => {
    if (event.target.value.trim() !== "") {
      setLnameIsValid(true);
    }
  };

  const EmailInputHandler = (event) => {
    if (event.target.value.trim() !== "") {
      setEmailIsValid(true);
    }
  };

  return (
    <form>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input type="text" id="Fname" onChange={FnameInputHandler} />
          {!FnameIsValid && <p>First Name is Error</p>}
        </div>

        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="Lname" onChange={LnameInputHandler} />
          {!LnameIsValid && <p>Last Name is Error</p>}
        </div>
      </div>

      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="email" id="email" onChange={EmailInputHandler} />
        {!EmailIsValid && <p>E-mail is Error</p>}
      </div>

      <div className="form-actions">
        <button disabled={FnameIsValid == false}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
