import { useState } from "react";
import useInput from "../hooks/use-Input";

const BasicForm = (props) => {
  const {
    enteredValue: Fname,
    valueChangeHandler: FnameChangeHandler,
    InputIsValid,
    BlurHandler: FnameBlurHandler,
  } = useInput((value) => value.trim() !== "");

  const [LnameIsValid, setLnameIsValid] = useState(true);
  const [EmailIsValid, setEmailIsValid] = useState(true);

  const FnameClass = FnameInputIsValid ? "ok" : "no";

  const LnameInputHandler = (event) => {
    if (event.target.value.trim() !== "") {
      setLnameIsValid(true);
    } else {
      setLnameIsValid(false);
    }
  };

  const EmailInputHandler = (event) => {
    if (event.target.value.trim() !== "") {
      setEmailIsValid(true);
    } else {
      setEmailIsValid(false);
    }
  };

  const submitHandler = (event) => {
    event.preventDefault();
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input
            className={FnameClass}
            type="text"
            id="Fname"
            onChange={FnameChangeHandler}
            onBlur={FnameBlurHandler}
          />
          {!FnameInputIsValid && <p className="ErrorP">First Name is Error</p>}
        </div>

        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input
            type="text"
            id="Lname"
            onChange={LnameInputHandler}
            onBlur={LnameInputHandler}
          />
          {!LnameIsValid && <p className="ErrorP">Last Name is Error</p>}
        </div>
      </div>

      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input
          type="email"
          id="email"
          onChange={EmailInputHandler}
          onBlur={EmailInputHandler}
        />
        {!EmailIsValid && <p className="ErrorP">E-mail is must have @</p>}
      </div>

      <div className="form-actions">
        <button disabled={FnameIsValid == false}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
