import React, { useState } from "react";

const useInput = (validateValue) => {
  const [enteredValue, setenteredValue] = useState("");
  const valueIsValid = validateValue(enteredValue);

  return {
    enteredValue,
    valueIsValid,
  };
};

export default useInput;
